package grechhub.cc.ua

import android.location.Location
import grechhub.cc.ua.data.Achievement


object AchivementInVilege {

    fun checkAchievements(locationtemp: Location):List<Achievement>
    {
        val listAchievement: MutableList<Achievement> = mutableListOf()

        listAchievement.addAll(checkGrechPody(locationtemp))
        listAchievement.addAll(checkKrasnoiPod(locationtemp))
        listAchievement.addAll(checkSvistunove(locationtemp))
        listAchievement.addAll(checkMirol(locationtemp))
        listAchievement.addAll(checkStepove(locationtemp))
        listAchievement.addAll(checkKravci(locationtemp))
        listAchievement.addAll(checkPologu(locationtemp))
        listAchievement.addAll(checkNewLife(locationtemp))
        listAchievement.addAll(checkOlecsandrivka(locationtemp))
        listAchievement.addAll(checkPodove(locationtemp))
        listAchievement.addAll(checkKrazove(locationtemp))
        listAchievement.addAll(checkVodane(locationtemp))
        listAchievement.addAll(checkOzerne(locationtemp))//мало инфо
        listAchievement.addAll(checkKalunivka(locationtemp))//мало инфо
        listAchievement.addAll(checkTrud(locationtemp))//мало инфо

        return listAchievement.toList()
    }

    fun checkGrechPody(locationtemp: Location):List<Achievement>
    {
        val info =Achievement(
            null,
            "Гречані Поди",
            R.drawable.achivement_villege,
            R.drawable.villege_grechpodu,
            "Найстарішими поселеннями в сільській раді є села Свистуново та Мар'їнфельд, або Підгорне. Мар'їнфельд в перекладі з німецького Мар'їна земля, було німецьким поселенням, воно поступово зникло, а жителі переселились в с. Свистунові. Перші хати в селі Свистуново за однією з версій були збудовані в 1894 році німецькими поселенцями. Нащадки цих переселенців заснували в 1915 році нове поселення - село Роза Люксембург (нині Гречані Поди)  (Колишня німецька колонія Ней-Лібенталь).\nТут же розташована і центральна садиба колективного сільськогосподарського підприємства «Калинівський».(1997р)\nГордістю КСП «Калинівський» є конеферма. Багато зусиль для відродження конярства доклав Микола Іванович Якимюк. Тепер радгосп має право брати участь у виставках, аукціонах, продавати коней.У 1951 році завезли 2 кобили угорсько-пруської породи з криничанського району і почалася підготовка до початку племінної справи.\nЯкщо згадати нашу історію аж до часів Січі, то на користь калинівцям буде твердження,   що   наші   предки,   легендарні   козаки-запорожці, вважалися неперевершеними майстрами верхової їзди і вважали коня мало не святим створінням.\nКрім української верхової породи, є англійська, орловська, торійська. Один з ентузіастів конярства Олександр Дзятко похвалився, що за жеребця Хусейна на всеукраїнській сільгоспвиставці в м. Дніпропетровську пропонували автомобіль «Вольво». Жеребець Цілиноград брав участь у змаганнях різних видів в СНГ (Москва, Дніпропетровськ).\n(Джерело: Широківщина В.Ганенко, Л.Бай) "

        )

        val listAchievement: MutableList<Achievement> = mutableListOf()
        val latitudeUpLeft =47.791670;    val longlatitudeUpLeft =33.454218
        val latitudeDownRight =47.779857;  val longlatitudeDownRight =33.473654
        if (locationtemp.latitude<latitudeUpLeft/*верхний левый*/){
            if (locationtemp.latitude>latitudeDownRight/*нижний правый*/){
                if (locationtemp.longitude>longlatitudeUpLeft/*верхний левый*/){
                    if (locationtemp.longitude<longlatitudeDownRight/*нижний правый*/){
                        return listOf(info)
                    }
                }
            }
        }

        return listAchievement.toList()
    }
    fun checkKrasnoiPod(locationtemp: Location):List<Achievement>
    {
        val info =Achievement(
            null,
            "Красний Під",
            R.drawable.achivement_villege,
            R.drawable.villege_krasnoipid,
            "В селі Красний Під проживало багато євреїв. Була ще й єврейська колонія Лекерта, яка раніше була центром сільської ради. В селі 1924 року збудовано школу. У 1937 р. тут відкрилась єврейська школа, яка після розкуркулювання заможних євреїв закрилася.\n" +
                    "(Джерело: Широківщина В.Ганенко, Л.Бай)"
        )

        val listAchievement: MutableList<Achievement> = mutableListOf()
        val latitudeUpLeft =47.762401;    val longlatitudeUpLeft =33.443175
        val latitudeDownLeft =47.756402;  val longlatitudeDownLeft =33.466940
        if (locationtemp.latitude<latitudeUpLeft/*верхний левый*/){
            if (locationtemp.latitude>latitudeDownLeft/*нижний правый*/){
                if (locationtemp.longitude>longlatitudeUpLeft/*верхний левый*/){
                    if (locationtemp.longitude<longlatitudeDownLeft/*нижний левый*/){
                        return listOf(info)
                    }
                }
            }
        }

        return listAchievement.toList()
    }
    fun checkSvistunove(locationtemp: Location):List<Achievement>
    {
        val info =Achievement(
            null,
            "Свистунове",
            R.drawable.achivement_villege,
            R.drawable.villege_svistunivo,
            "Протягом останньої чверті ХУШ ст. адміністративно-територіальний поділ Південної України зазнав неодноразових змін. Утворювались губернії, які пізніше реорганізовувались у намісництва. Нарешті, відповідно до Указу Павла І від 1796 року, замість Катеринославського та Вознесенського намісництв і Таврійської області\n" +
                    "була організована Новоросійська губернія.\n" +
                    "З кінця ХУШ століття безмежні степи Північного Причорномор'я почали швидко заселятись. На нові землі прибули втікачі від соціального гніту з Правобережної та Лівобережної України, центральних губерній Росії. В південну Україну прибуло також чимало іноземних колоністів: сербів, болгар, молдован, німців.\n" +
                    "Під час семирічної війни граф Румянцев звернув свою увагу на взірцеві господарства меннонітів, німців, лютеранів. Перше поселення німців було в районі впадання в Дніпро р. Мокра Сура. Саме в цей період - кінець ХУШ століття - з'явились перші німці-переселенці на території нашого сучасного Широківського району. Тоді ж виникають села Свистуново і Мар'їнфельд. Таким чином, датою їх заснування слід вважати 1798 рік.\n" +
                    "Німці принесли культуру землеробства. Почали культивувати нові сільськогосподарські високоврожайні культури, завезли високопродуктивну худобу, запровадили західноєвропейську культуру будівництва населених пунктів. Якщо до цього сільська будівля була глинобитною, то з переселенням німців на західній околиці с. Свистуново була побудована цегельня, де випалювалась цегла і черепиця, які йшли на будівництво сіл. Німецькі майстри, крім різноманітних глиняних виробів, вміли виготовляти керамічні домовини, на які мешканці села зараз\n" +
                    "натрапляють під час поховань.\n" +
                    "Відведені землі належали трьом німецьким землевласникам, які жили в Свистуново і мали розкішні приватні садиби, поряд яких розташовувались більш скромні будівлі мешканців, які наймитували у багатих.\n" +
                    "Мар'їнфельд було засновано як допоміжний населений пункт, в якому проживали люди, що служили землевласникам із Свистуново. Перший ставок, на березі якого стояв Мар'їнфельд, сягає своєю історією в сиву давнину. У ті далекі часи він слугував місцем відпочинку, навколо нього були розташовані загони для худоби і птахів, власниками яких були свистунівські господарі. Обабіч буяли зелені насадження та пасовиська з сокови¬тими багаторічними травами. Сіна тут було настільки багато, що заготовленням його займалися за невелику\n" +
                    "платню мешканці села Широкого.\n" +
                    "Мар'їнфельд, а особливо Свистуново, мали привабливий вигляд. З кінця в кінець вулиці проходив високий суцільний цегельний мурок через усе село з напівовальними арками-воротами на в'їздах до дворів. На кожній садибі красувались цегляні будинки, кожен господар мав величезних розмірів кам'яні погреби з куполоподібними\n" +
                    "зводами.\n" +
                    "Під час революції (1917 рік) та наступного безвладдя Свистуново та Мар'їнфельд піддавалися жорстоким\n" +
                    "пограбуванням банд з Широкого.\n" +
                    "В 1920 році із 9 волостей Верхньодніпровського повіту і частини повітів Миколаївської губернії: Олександрівського, Єлисаветградського і Херсонського утворюється новий повіт - Криворізький.\n" +
                    "В 1923 році центральна адміністративно-територіальна комісія ВЦВК 13 лютого затвердила новий адміністративно-територіальний поділ, за яким Катеринославська губернія реорганізована в 7 округів. (Повіт\n" +
                    "стає Криворізьким округом).\n" +
                    "Жителі села в короткий час справились з післяреволюційною розрухою і вже за переписом населення в 1925 році до складу Олександрівської сільської ради (село Олександрівка до 1917 року називалось Новоолександрів-кою) входить 8 населених пунктів, в тому числі село Свистуново.\n" +
                    "До самої війни Свистуново і Мар'їнфельд розквітали і основними4 жителями були німці. Жили вони і під час окупації аж до наближення фронту, а потім примусово виїхали разом з відступаючими німецькими військами.\n" +
                    "Під час наступу наших військ (в лютому 1944 року) с. Мар'їнфельд дуже постраждало, командуванню змушене було застосувати ракетні установки «Катюша», поскільки в населеному пункті знаходилось сильне угрупування ворога. Після війни в селі продовжували жити люди, а згодом його спіткала доля безперспективного, як і багато інших населених пунктів.\n" +
                    "В Свистуново залишились порожні будівлі, здебільшого зруйновані. В них поселились жителі із навколишніх сіл: Потороча Дмитро, Авраменко Дмитро, Соболева Олександра, Шешко Василь, Мущенко Лазар, Кисильов Тарас та інші (1944 p.), а в 1945 році приїхали сім'ї депортованих українців з колишніх польських земель. Це -Климюки, Дзятки, Дашки, Іванчуки, Колесники, Войцики, Харчуки... В 1952 році приїхали переселенці з Західної України. їх розселяли по квартирах, а згодом з допомогою колгоспу вони побудували свої будівлі. На місці\n" +
                    "старих зруйнованих хат піднімались нові гарні будинки. До 1950 року село Свистуново було окремим колгоспом імені К. Лібкнехта. Першим головою колгоспу був Авраменко Дмитро Гнатович, після нього невеликий проміжок v часу колгоспом керував Сакович (з Подового), а потім - відданий своїй справі Горб Яків Іларіонович, рільником був Шешко Василь Романович.\n" +
                    "Відразу після визволення (1944 р.) колгосп мав дві корівки і десяток коней (кілька з яких підірвались на мінах, бо околиця села була замінована аж до 1946 року). Ферми створені були за рахунок приватного сектора: колгоспники добровільно віддали свою худобу. Сіяли також позиченим у людей зерном. Після збору врожаю з боргами розрахувались. Працівники не шкодували сил, ледарів не було, працювали дружно, відпочивали по кілька годин на добу. Вдень жнеш, в'яжеш снопи, вночі їх складаєш, або ж вдень молотиш, а вночі вієш ручною віялкою. Вручну навантажуєш на підводи, відправляєш на елеватор на Радушне. Це треба встигнути зробити за ніч, бо вдень чекає інша термінова робота. Спочатку землю обробляли волами і коровами. З появою стареньких тракторців ХТЗ, «Універсал» колгоспники одержали змогу швидше виконувати роботу. Контроль був скрупульозний: і вночі міг з'явитись представник району, (виміряти, наприклад, глибину оранки і, не дай Бог, виявить, що зорано менше ніж на ЗО сантиметрів...) Про крадіжки не було і мови. Та колгоспники і без такої пильноти працювали добросовісно і дбайливо виконували доручену справу. Кожен намагався зробити як можна більше, обов'язково перевиконати завдання. В період оранки працювали і вночі. Попереду йшла людина борозною і несла ліхтар, а за вогником-орієнтиром йшов трактор з одним плугом. На полі з кожного краю стояло по З діжки, Якщо спорожнювався радіатор, несли відрами доливати, інколи на відстань більше півкілометра. Молодь і люди несімейні увесь літній період були у полі. Трактори, жатки, інший реманент теж знаходився весь сезон на полях, бо дуже економили пальне і берегли механізми. Використовували їх тільки за призначенням, про холості пробіги ніхто не міг і подумати. Техніка і без того була така слаба, що доводилось ремонтувати без кінця. Ретельно виконували роботи трактористи Забело Петро, Литвин Сергій, Климюк Василь, причеплювачі Міщук Надія, Цибулько Клавдія. Збігати з поля по мастило, запчастини (а село за 3-4 кілометри), поки трактор ремонтується, не вважала за проблему 19-річна Настя Шешко. Пальне підвозив Ільченко Кирило. На літньому стані готувала смачні обіди і пекла запашний хліб Шешко Марфа Іванівна.\n" +
                    "Поступово добробут трудівників зростав. В селі з'явився корівник, де господарювала Горвазюк Явдокія і її сестри Катя і Надя. Вони не тільки доїли корів, а й підвозили корм, напували худобу (воду діставали цебром з колодязя, а в зимовий період до цього треба було зусиль докласти нелюдських), чистили ферми. Доглядали свиней на свинофермі Жукова Марія, Рева Фрося. Конюхами були Дзятко Феофіл та Климюк Йосип. Якщо на кожній з цих ферм було до 20 тварин, то у вівчарні овець мали кілька тисяч. І випасав, і стриг їх Яків Гаврилюк зі своєю сім'єю. План по здачі вовни і м'яса виконувався. На птахофермі було близько 3 тисяч птахів. Біля них клопотались мати і дочка Ольшевські Катерина і Фрося, пізніше Петриченко Катерина. Майстрами на всі руки можна назвати Поторочу Дмитра, Дзятка Сергія. Рільницькі ланки в своєму складі налічували більше 20 молодих сильних старанних дівчат. Женя Григоренко, Галя Кисильова, Женя Климюк, Надя Короконда, Мущенко Тоня і Ліда.\n" +
                    "Були взірцеві поля: ні грудочки, ні бур'янцю - «Хоч каші кинь». Можна було босими ногами бігти з краю в край і не пораниш, і не вколеш, земля, як пух. А тому й пшениця зерно в зерно, качани кукурудзи як на підбір. ї ячмінь, і жито, і просо... Мав колгосп і кузню, пишався справжнім майстром - ковалем Устиновим О.С., а молотобойцем вправним був Шешко Іван. Відбудували цегельню, яка справно давала цеглу і черепицю не тільки для своїх потреб, а й виконувала замовлення інших господарств. Шевченко - досвідчений, літній вже чоловік - гончарував тут. Випалювався і череп'яний посуд, який бричкою возили на базар до міста.\n" +
                    "Оплата праці була натуральною. Частину заробленого віддавали облігаціями - на 20 років державна позика. Іншу частину після збирання врожаю - зерном, на зароблений тру¬додень - від 200 грамів до 2 кілограмів зерна. Старанний працівник міг за рік виробляти до 700 трудоднів. Грошима не одержували нічого. Щоб щось купити, продавали зерно чи якусь живність (колгоспники тримали худобу - свиней, овець, гусей, курей). V В 1950 році Свистуново приєднали до колгоспу імені Дзержинського (с. Рози Люксембург). Головами колгоспу були Дрига, Малевич, Сіпаренко.\n" +
                    "Укрупнення господарств негативно відбилося на продуктивності землеробства і тваринництва. До цього колгоспники були істинними господарями і конкретно бачили плоди своєї праці. Дбайливо доглядався кожен клаптик землі, з якого отримували максимальну І віддачу. Кожна тварина, старанно нагодована, була високопродуктивною. Прискіпливо вівся облік одержаної продукції, збут якої здійснювався колективно. У 1960 році землі села Свистуново були об'єднані з Миролюбівкою, центральною садибою стало село Миролюбівка. Саме в ці роки була побудована недосконала зрошувальна система\n" +
                    "(Джерело: Широківщина В.Ганенко, Л.Бай)"
        )

        val listAchievement: MutableList<Achievement> = mutableListOf()
        val latitudeUpLeft =47.791539;    val longlatitudeUpLeft =33.402441
        val latitudeDownLeft =47.783149;  val longlatitudeDownLeft =33.422505
        if (locationtemp.latitude<latitudeUpLeft/*верхний левый*/){
            if (locationtemp.latitude>latitudeDownLeft/*нижний правый*/){
                if (locationtemp.longitude>longlatitudeUpLeft/*верхний левый*/){
                    if (locationtemp.longitude<longlatitudeDownLeft/*нижний левый*/){
                        return listOf(info)
                    }
                }
            }
        }

        return listAchievement.toList()
    }
    fun checkMirol(locationtemp: Location):List<Achievement>
    {
        val info =Achievement(
            null,
            "Миролюбівка",
            R.drawable.achivement_villege,
            R.drawable.villege_mirol,
            "На південному сході від Кривого Рогу розташоване село Миролюбівка. Колись це були цілинні чудові\n" +
                    "чорноземні степи, порізані великими балками Велика Кроква і Мала Кроква. У XIX столітті  землі, які пролягали\n" +
                    "від села Войкове до селища Радущного і нараховували 6 тисяч десятин, були привласнені чиновником з міста\n" +
                    "Херсона Яновським. Така ж кількість землі Яновського була і в Бесарабії, яку він подарував своєму зятеві.\n" +
                    "Керував своїм господарством Яновський наїздами з Херсона, де він займав високу посаду, а конкретним\n" +
                    ". господарем на землі був управитель.\n" +
                    "Напрямок господарства - тваринницький. Сільськогосподарські культури вирощувались в основному для потреб тваринництва. Культивувалися високопородні вівці, яких було десятки тисяч голів. Ще й зараз можна по залишках будівельних матеріалів між садами Свистуново і Миролюбівка окреслити кордони панської економії, де були розташовані господарські будівлі, кошари для овець та бараки для наймитів.\n" +
                    "Після революції 1917 року деякий час ці землі були без певного господаря, тому використовувались криворізькими шахтарями під городи. Деякі криворіжці почали будувати землянки і залишались на круглий рік на цій землі.\n" +
                    "Після голодного 1922 року почали переселятись білоруси з Могильовської і Мінської областей. У 1923 році білорусами-переселенцями та криворізькими шахтарями було засновано село. Поскільки приїжджий люд був мирним і трудолюбивим, тому і село нарекли Миролюбівкою.\n" +
                    "Перші переселенці білоруси: Верщки, Івановські, Короленки, Поташі, Шарки, Демошкевичі. і переселенці криворізьких копалень: Смоквіни, Лавроненки, Стадники, Криштопи, Столітні, Чумаки, Архипенки, Сичі, Чумаченки. Пізніше переїхали Безпальки з Кіровоградщини, Ісаченки з Сергіївки, Підрядови, Шевченки, Білостоцькі з Олександрівки.\n" +
                    "V У 1939 році після змін, які відбулися на західному кордоні України, до села з території колишніх польських земель, були переселені родини Кеців, Норовичуків, Матиюків.\n" +
                    "У 1923 році село будувалося власними силами переселенців і мало Г-подібну форму:   центральна вулиця строго зі сходу на захід і допоміжна - з півночі на південь. Для будівництва хат використовувались глина і солома. Жителі займалися землеробством, кожна родина мала по 5-Ю десятин землі. Господарства були приватними. За короткий час були вирощені великі фруктові сади, територія села буяла декоративними насадженнями.\n" +
                    "У 1930 році було створено колгосп, який очолив Лука Іванович Козак. Його послідовниками були Олександр Олександрович Кушнір, Сушко з села Лозуватки, Кривошеєв, Забело Володимир Микитович, Вербенець Борис Семенович, Горбач Веремій Сергійович, Вовченко Микола Васильович - Герой Соціалістичної Праці, Пуга Всеволод Пантелійович, Крейса Михайло Акимович, Андрух Василь Миколайович, Козаченко Микола Васильович.\n" +
                    "І вже в 1935 році в господарстві налічувалось 800 га землі, 85 коней, 25 корів, 120 свиней, 150 голів овець. В цьому ж році було закладено на східній околиці села сад на 100 га, який очолив Павло Савелійович Івановський. Посадковий матеріал вирощували у власному розсаднику.\n" +
                    "Люди повірили в нову систему господарювання і працювали не покладаючи рук. Жили дружно, весело, косили жатками, жали серпами, вручну в'язали снопи. Першими будівничими колгоспу були: П. Т.Безпалько (брига¬дир), Ф. Короленко, Г. Д. Дерев'янко (делегат II Всесоюзного з'їзду колгоспників-ударників, яка все життя гордилася зустріччю з Калініним та червоною косинкою, яку подарувала їй Надія Костянтинівна Крупська), Вершок М., Вершок.Є., Вершок Ю., Вершок А., Івановський Я. (кавалер ордена Леніна), Івановський С, Івановський П., Івановська О. та інші.\n" +
                    "До початку Великої Вітчизняної війни артіль імені Сталіна набула значного економічного розвитку. Були побудовані свиноферма, конюшня, корівник, млин, баня, контора, клуб.\n" +
                    "Під час окупації жителі села працювали у господарстві. Мешканцям села жилося сутужно у фашистській неволі, але їх існування було пом'якшено діями коменданта окупаційного режиму Шпікермана - людини, позбавленої фашистських поглядів і переконань.\n" +
                    "Холодний світанок 18 лютого 1944 року можна вважати другим днем народження Миролюбівки. Визволення від фашизму принесли воїни 46-ї гвардійської армії генерала Глаголєва Василя Васильовича, безпосередню участь у боях за населені пункти брали воїни 48-ї гвардійської стрілецької дивізії під командуванням гвардії генерал-майора Корчикова, яка входила до складу 34 стрілецького корпусу генерала Кособуцького (пізніше Героя Радянського Союзу). Безпосередньо визволяли Миролюбівку воїни 143 гвардійського стрілецького полку під командуванням майора Берлінського.\n" +
                    "\"-\"Завдяки розумному командуванню підрозділів, село великих руйнацій не зазнало, основні сили наших військ діяли у напрямках східної та західної околиць, залишаючи противника в оточенні. Німецькі загарбники, побоюючись потрапити в полон, поспішно залишили село. Особливої жорстокості бої за Миролюбівку набули у західній частині села, оскільки ворог здійснював сильний артналіт з боку села Іванівки (територія сучасного ПГЗКа). Тільки прикриваючись бронею танків, наші воїни могли просуватися вперед.\n" +
                    "У лютому 1944 року, коли радянські війська визволили Миролюбівку, жителі дружно взялися за відбудову зруйнованого. Невдовзі до миролюбівського колгоспу приєдналася сусідня артіль імені Леніна (село Садове, яке потрапило під знесення). Разом відновлювали зруйновані війною господарства, розвивали колгоспну економіку. До праці дружно взялися ветерани, що повернулися: Абросов П.Л., Абрамчук К.Г., Архипенко В.І., Авдєєв Ф.Ф., Бойко М.К., Вершок М.М., Вербенець Б.С., Вовченко М.В., Вербенець М.С., Григоренко І.Є., Горбач B.C., Галачов Ф.А., Дакуко М.І./Дзятко М.В., Журавель П.І., Забело В.М., Ісаченко І.К., Івашко Я.С., Козак В.Л., Кольсун А.Ф., Кучин М.С., Курінчанін П.С., Коношевський Ф.С., Ковальчук Г.І., Косорук С.П., Конощук Н.І., Лисенко Л.Д., Лисенко Ю.Д., Ляшенко П.М., Мітла O.K. та інші.\n" +
                    "В 1949 році господарство очолив .недавній, весь зранений, фронтовик Вовченко Микола Васильович -людина до нестями віддана селу і людям під його керівництвом колгосп почав стрімко набувати вершин свого розвитку. Звелися нові виробничі об'єкти: свинарник, чотирирядний корівник, птахомістечко з власним інкубатором, пасіка, автогараж, дві водонапірні вежі, насаджені нові сади, виноградник на площі 130 га, ягідники, закладені парники. Велику увагу приділялось соціальному і культурному розвитку села: були зведені восьмирічна школа, Будинок культури, дитячий садок, магазин, закладено парк.\n" +
                    " Колгосп набув нечуваного економічного розвитку. Господарство не раз у післявоєнні роки завойовувало право брати участь у Всесоюзній виставці досягнень народного господарства СРСР, а в 1960 році було нагороджено дипломом І ступеня. Таких результатів змогли досягти завдяки невтомній праці голови колгоспу Вовченка Миколи Васильовича, якому було першому в області присвоєно звання Героя Соціалістичної праці, головного зоотехніка Кульбіди, головного бухгалтера Чоботаря Григорія, бригадира будівельної бригади Вербенця Миколи, мулярів Безпалька Івана, Обремського Володимира, бригадира польової бригади Проходи Данила, завідуючого свинофермою кавалера ордена Леніна Івановського Якова, пташниць Архипенко Антоніни, Івановської Марії, Вершок Марії, Вершок Єлизавети, Горбач Валентини - кавалера ордена Леніна, ланкових Шищової Ганни, Ісаченко Явдокії, Забело Олександри, Архипенко Олександри, Чумаченко Ніни та інших.\n" +
                    "Овочівництво стало однією із провідних галузей виробництва завдяки ентузіасту цієї і справи Андрухіна.\n" +
                    "Значних успіхів досягли тваринники, незважаючи на відсутність механізації виробництва. Завідуюча фермою Сазонович Ліза, доярки Задирака Марія, Малих Тетяна, Вовченко М.В, Зубова Рая, Пузик Ганна, Сич Марія, М'ядіна Віра, Рябенко Єва, Пихнівська Марія, Хомула Ганна, тваринник Пав лік Федір, Заболоцька.\n" +
                    "Тракторну бригаду очолив Мисник Олексій Пантелійович. Основний внесок в розвиток господарства зробили механізатори Забело Федір, Архипенко Василь, Короленко Павло, Кучин Микола, Шульга Володимир, Литвин Володимир, Кец Павло, водії Ляшенко Павло, Горбач Веремій, Унгурян Олексій, Сич Микола, Онищенко Михайло, Столітній Олександр, Смульський Мар'ян, ковалі Шишов Дмитро, Ницько Григорій, Чумак Григорій, Колячонок, Авдєєв.\n" +
                    "У 1960 році відбулася реорганізація колгоспів, в результаті якої землі Миролюбівки і Свистуново були об'єднані в одне господарство. З 1961 року колгосп очолив Всеволод Пантелійович Пуга, який зміг утворити дієздатний керівний склад. Шломенко К. П. - головний агроном, Слюсар В. Д. - головний зоотехнік, Рябошапка М. Я. -головний бухгалтер, Мисник О. П. - головний інженер, Бойко В. С. - головний економіст. Пеліпака І. Д., Шешко І. В. - керуючі відділками, Гришальов В. Ф., Куриц І. П. - бригадири тракторних бригад, Старостенко П.С. - головний ветлікар, Лисов Є. Є. - економіст, Вербенець М. С. - бригадир будівельної бригади.\n" +
                    "До 70-х років досягли значних успіхів у виробництві. Так, в 1962-64 pp. урожайність зернових становила 41,1 центнера з гектара, вироблено м'яса 1,7 тис. цн, надій молока на фуражну корову становив 3500 літрів.\n" +
                    "Досягнені успіхи дали змогу виконати п'ятирічний план за чотири роки з такими показниками: вироблено зерна 14174 т, м'яса 755 т, молока 6528 т, яєць 3 млн. шт. За багатьма показниками хлібороби і тваринники працювали в рахунок 1971 року.\n" +
                    "З кожного гектара у 1969 році взяли найвищий в області урожай зернових. Добре вродили і соняшник, кормові культури, овочі, картопля.\n" +
                    "Держава високо оцінила роботу сільських трударів, нагородивши колгосп «Ленінською\n" +
                    "ювілейною почесною грамотою». Удостоєний почесного звання Героя Соціалістичної Праці -\n" +
                    "Гришальов Віктор Федорович. Звання заслуженого працівника сільського господарства - ».\n" +
                    "Шешко Іван Васильович, Орденами Леніна - Сич М.С., Пуга В.П., Карпенко О.Л., Косорук \n" +
                    "О.С., Шворак М.С., Шворак І.Х.\t\n" +
                    "На польовому стані тракторної бригади є добре обладнана майстерня, якою завідував бригадир Смульський Мар'ян Феліксович. Токарі Євген Озеров, Слюсар Володимир Заболоцький мали змогу швидко відремонтувати несправність у машин. А механізатори набули багатьох суміжних професій: слюсаря, токаря, зварювальника. Це Іван Скляр, Іван Бойко, Василь Попенко, Павло Логвинюк, Віктор Горбач, Шевчук Григорій, Микола Кулініч, Сич Геннадій, Яків Трохименко, Литвин Григорій та інші. Професія механізатора стала не сезонною, а постійною, високооплачуваною.\n" +
                    "Особливий нагляд за телятами племінного ядра - майбутніми високопродуктивними коровами - здійснювали тваринники Волосевич Іван, син одного з перших на Криворіжжі Героїв Радянського Союзу - Петра Семеновича Волосевича, Варвара Дзятко, Микола Архипенко, Микола Сахончик, Григорій Хомула, Олександр Бунцевич, Степан Галаша, Лідія Климук та інші.\n" +
                    "Тваринники постійно змагалися не тільки за кількість, а й за високу якість продукції. Так, 95 відсотків молока відправляли державі 1-м сортом. Доярки перейшли чотиритисячний рубіж надоїв молока на кожну фуражну корову. Це Марія Пихнівська, Єва Рябенко, Раїса Зубова, Волосевич Ганна, Горбач Катерина, М'ядіна Віра, Кулініч Марія, Куриць Марія, Боярина Валентина, Марія Піценко та інші. А очолили ферми досвідчені працівники Бунцевич Є., Піценко І., Білич П.\n" +
                    "Неподалік села розкинулась птахоферма   -  справжнє містечко з водопроводом, паровим опаленням, електрикою. Господарство це очолила Ніна Костянтинівна Кульбіда.\n" +
                    "(Джерело: Широківщина В.Ганенко, Л.Бай)\n"
        )

        val listAchievement: MutableList<Achievement> = mutableListOf()
        val latitudeUpLeft =47.82645;    val longlatitudeUpLeft =33.405514
        val latitudeDownLeft =47.803594;  val longlatitudeDownLeft =33.427143
        if (locationtemp.latitude<latitudeUpLeft/*верхний левый*/){
            if (locationtemp.latitude>latitudeDownLeft/*нижний правый*/){
                if (locationtemp.longitude>longlatitudeUpLeft/*верхний левый*/){
                    if (locationtemp.longitude<longlatitudeDownLeft/*нижний левый*/){
                        return listOf(info)
                    }
